mapper.update:
UPDATE COMMENTS
SET com_contents = #{com_contents}
WHERE com_idx = #{com_idx}

UPDATE POPUP_BOARD
SET thumb = NULL
WHERE board_idx = #{board_idx}

UPDATE POPUP_BOARD
SET likes_count = #{likes_count}
WHERE board_idx = #{board_idx}

mapper.select:
SELECT * FROM POPUP_BOARD 
WHERE writer = #{writer}
ORDER BY postdate DESC
